countries_dict = {"Canada": {"description": "Canada is a large country in North America. The capital of Canada is Ottawa.", "tourist destinations": " Some popular tourist attractions in Canada include Niagara Falls, Banff National Park, and the CN Tower.", "image": "https://i.imgur.com/oFDj6Kd.jpeg"}, "United States": {"description": "America, or the United States is a country primarily located in North America. It shares borders with Canada and Mexico and its national capital is Washington D.C.", "tourist destinations": " Some great tourist attractions in the US are the Grand Canyon, the Statue of Liberty, the Walt Disney World Resort, and the White House", "image": "https://cdn.pixabay.com/photo/2014/02/17/10/20/statue-of-liberty-267948_960_720.jpg"}, "Japan": {"description": "Japan is an island country in East Asia. Tokyo is its capital and it is also its largest city.", "tourist destinations": "Some popular tourist attractions include Mt. Fuji, the Imperial Palace, and Hiroshima Peace Memorial Park.", "image": "https://cdn.pixabay.com/photo/2014/10/07/13/48/mountain-477832_960_720.jpg"}, "United Kingdom": {"description": "The UK is a country in Europe. The capital of the United Kingdom is London.", "tourist destinations": "Some examples of tourist attractions there include Stonehenge, the Tower of London, and the Roman Baths and Georgian City of Bath.", "image": "https://cdn.pixabay.com/photo/2014/11/13/23/34/palace-530055_960_720.jpg"}, "France": {"description": "France is a country in Western Europe. Its capital is Paris.", "tourist destinations": "Some well-known attractions in France include the Eiffel Tower, the Musée du Louvre, and the Château de Versailles.", "image": "https://cdn.pixabay.com/photo/2013/04/11/19/46/building-102840_960_720.jpg"}, "Italy": {"description": "Italy is a country in Europe. It is located in the Mediterranean Sea and its capital is Rome.", "tourist destinations": "Some popular tourist attractions in Italy include the Colosseum, Florence Duomo Santa Maria del Fiore, and The Grand Canal in Venice.", "image": "https://cdn.pixabay.com/photo/2014/03/03/16/12/village-279013_960_720.jpg"}, "South Korea": {"description":"South Korea is a country in East Asia. Its capital is Seoul.", "tourist destinations": "Some well-known attractions in South Korea include the Changdeokgung Palace, the N Seoul Tower, and the Bukchon Hanok Village", "image": "https://cdn.pixabay.com/photo/2020/08/09/11/31/business-5475283_960_720.jpg"}, "China": {"description": "China is a country in East Asia. It is a country with a very high population, and its capital is Beijing.", "tourist destinations": "Some well-known tourist attractions in China include the Great Wall of China, the Forbidden City & the Imperial Palace, the Terracotta Army, and the Summer Palace.", "image": "https://cdn.pixabay.com/photo/2020/08/12/19/28/great-wall-of-china-5483516_960_720.jpg"}, "Germany": {"description": "Germany is a country in Central Europe. It is a country with a high population and its capital is Berlin.", "tourist destinations": "Some nice tourist attractions in Germany include the Brandenburg Gate, the Cologne Cathedral, and the Black Forest.", "image": "https://cdn.pixabay.com/photo/2014/11/15/23/29/fairytale-532850_960_720.jpg"}, "Russia": {"description": "Russia is a country in Eastern Europe and Northern Asia. It is the largest country in the world and its capital is Moscow.", "tourist destinations": "Some tourist attractions in Russia include the Saint Basil’s Cathedral, the Hermitage Museum, and Moscow Kremlin.", "image": "https://cdn.pixabay.com/photo/2016/07/30/08/13/moscow-1556561_960_720.jpg"}, "Netherlands": {"description": "The Netherlands is a located in northwestern Europe. It also has overseas territory in the Caribbean and its capital is Amsterdam.", "tourist destinations": "Some well-known tourist attractions include the Jordaan and Amsterdam's Canals, the Keukenhof, and the Anne Frank House.", "image": "https://cdn.pixabay.com/photo/2020/09/18/21/12/buildings-5582974_960_720.jpg"}, "India": {"description": "India is a country in South Asia. The capital of India is New Delhi.", "tourist destinations": "Some popular tourist destinations include the Taj Mahal, the Holy City of Varanasi, and the Harmandir Sahib.", "image": "https://www.planetware.com/photos-large/IND/india-top-attractions-taj-mahal.jpg"}, "Australia": {"description": "Australia is a country on the Australian continent. The capital of Australia is Canberra.", "tourist destinations": "Some well-known attractions include the Sydney Opera House, the Great Barrier Reef Marine Park, and Uluru-Kata Tjuta National Park.", "image":"https://cdn.britannica.com/49/20349-050-C5029C80/Uluru-Ayers-Rock-Uluru-Kata-Tjuta-National-Park-Australia.jpg"}}